import React from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';

const Container = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
  gap: ${props => props.theme.spacing.xl};
`;

const UnitCard = styled(Link)`
  display: block;
  background: ${props => props.theme.colors.surface};
  border-radius: ${props => props.theme.borderRadius.lg};
  padding: ${props => props.theme.spacing.xl};
  text-decoration: none;
  color: inherit;
  transition: ${props => props.theme.transitions.default};
  box-shadow: ${props => props.theme.shadows.sm};

  &:hover {
    transform: translateY(-4px);
    box-shadow: ${props => props.theme.shadows.lg};
  }
`;

const UnitTitle = styled.h2`
  font-size: 1.5rem;
  font-weight: 700;
  color: ${props => props.theme.colors.text};
  margin-bottom: ${props => props.theme.spacing.md};
  display: flex;
  align-items: center;
  gap: ${props => props.theme.spacing.sm};
`;

const UnitDescription = styled.p`
  color: ${props => props.theme.colors.textSecondary};
  margin-bottom: ${props => props.theme.spacing.lg};
  line-height: 1.6;
`;

const FeatureList = styled.ul`
  list-style: none;
  margin: 0;
  padding: 0;
  display: flex;
  flex-direction: column;
  gap: ${props => props.theme.spacing.sm};
`;

const FeatureItem = styled.li`
  display: flex;
  align-items: center;
  gap: ${props => props.theme.spacing.sm};
  font-size: 0.875rem;
  color: ${props => props.theme.colors.textSecondary};
`;

const ViewButton = styled.div`
  margin-top: ${props => props.theme.spacing.lg};
  padding: ${props => props.theme.spacing.md} ${props => props.theme.spacing.lg};
  background: ${props => props.theme.colors.primary};
  color: ${props => props.theme.colors.textInverse};
  border-radius: ${props => props.theme.borderRadius.md};
  text-align: center;
  font-weight: 600;
  transition: ${props => props.theme.transitions.default};
`;

const hydroUnits = [
  {
    id: 'DWC1',
    title: 'Deep Water Culture 1',
    description: 'Primary hydroponic unit with full sensor monitoring and automated control systems.',
    features: [
      'ðŸ§ª pH & TDS monitoring',
      'ðŸŒ¡ï¸ Temperature sensors (8 levels)',
      'ðŸ’§ Water level monitoring',
      'ðŸ’¡ LED lighting control',
      'ðŸŒ€ Fan ventilation',
      'âš™ï¸ Automated pump cycles'
    ]
  },
  {
    id: 'DWC2',
    title: 'Deep Water Culture 2',
    description: 'Secondary hydroponic unit with identical monitoring capabilities.',
    features: [
      'ðŸ§ª pH & TDS monitoring',
      'ðŸŒ¡ï¸ Temperature sensors (8 levels)',
      'ðŸ’§ Water level monitoring',
      'ðŸ’¡ LED lighting control',
      'ðŸŒ€ Fan ventilation',
      'âš™ï¸ Automated pump cycles'
    ]
  },
  {
    id: 'DWC3',
    title: 'Deep Water Culture 3',
    description: 'Third hydroponic unit for expanded growing capacity.',
    features: [
      'ðŸ§ª pH & TDS monitoring',
      'ðŸŒ¡ï¸ Temperature sensors (8 levels)',
      'ðŸ’§ Water level monitoring',
      'ðŸ’¡ LED lighting control',
      'ðŸŒ€ Fan ventilation',
      'âš™ï¸ Automated pump cycles'
    ]
  },
  {
    id: 'DWC4',
    title: 'Deep Water Culture 4',
    description: 'Fourth hydroponic unit with advanced monitoring systems.',
    features: [
      'ðŸ§ª pH & TDS monitoring',
      'ðŸŒ¡ï¸ Temperature sensors (8 levels)',
      'ðŸ’§ Water level monitoring',
      'ðŸ’¡ LED lighting control',
      'ðŸŒ€ Fan ventilation',
      'âš™ï¸ Automated pump cycles'
    ]
  },
  {
    id: 'DWC5',
    title: 'Deep Water Culture 5',
    description: 'Fifth hydroponic unit completing the full system array.',
    features: [
      'ðŸ§ª pH & TDS monitoring',
      'ðŸŒ¡ï¸ Temperature sensors (8 levels)',
      'ðŸ’§ Water level monitoring',
      'ðŸ’¡ LED lighting control',
      'ðŸŒ€ Fan ventilation',
      'âš™ï¸ Automated pump cycles'
    ]
  }
];

const HydroUnits = () => {
  return (
    <Container>
      {hydroUnits.map((unit) => (
        <UnitCard key={unit.id} to={`/hydro-units/${unit.id}`}>
          <UnitTitle>
            ðŸŒ± {unit.title}
          </UnitTitle>
          <UnitDescription>{unit.description}</UnitDescription>
          <FeatureList>
            {unit.features.map((feature, index) => (
              <FeatureItem key={index}>
                {feature}
              </FeatureItem>
            ))}
          </FeatureList>
          <ViewButton>
            View {unit.id} Details â†’
          </ViewButton>
        </UnitCard>
      ))}
    </Container>
  );
};

export default HydroUnits;
